import React from 'react';
import { Box, Typography } from '@mui/material';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import MainConfig from '../components/MainConfig';
import BotStatus from '../components/BotStatus';
import Products from '../components/Products';

const ConfigPage: React.FC = () => {
  return (
    <Box>
      <Header />
      <Box display="flex">
        <Sidebar selected="config" />

        <Box flexGrow={1} sx={{ backgroundColor: '#ffffff', padding: '24px' }}>
          {/* Header interior */}
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
            <Typography variant="h5" sx={{ fontFamily: "Poppins", fontWeight: "bold" }}>
              Configuración de Chatbot
            </Typography>
            <Box>
              <BotStatus />
            </Box>
          </Box>

          {/* Resto de la configuración */}
          <Box sx={{ display: 'flex', gap: 3, flexWrap: 'wrap' }}>
            <Box sx={{ flex: { xs: '100%', md: '70%' } }}>
              <MainConfig />
            </Box>
            <Box sx={{ flex: { xs: '100%', md: '30%' } }}>
              <Products />
            </Box>
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default ConfigPage;
