import { useState } from 'react';
import { Business, FAQ } from '../types';

const MainConfig: React.FC = () => {
  const mockBusinessData: Business = {
    id: 1,
    name: 'La Pizzer√≠a Italiana',
    type: 'restaurante',
    location: 'Buenos Aires',
    address: 'Av. Corrientes 123',
    hours: {
      Lunes: { open: '12:00', close: '23:00' },
      Martes: { open: '12:00', close: '23:00' },
      Mi√©rcoles: { open: '12:00', close: '23:00' },
      Jueves: { open: '12:00', close: '23:00' },
      Viernes: { open: '12:00', close: '23:00' },
      S√°bado: { open: '12:00', close: '23:00' },
      Domingo: { open: '12:00', close: '23:00' },
    },
    isActive: true,
  };

  const mockContext = 'Eres el contestador de la pizzer√≠a Bongusto en Buenos Aires...';
  const mockFaqs: FAQ[] = [
    { question: '¬øTienen delivery?', answer: 'S√≠, hacemos env√≠os a domicilio.' },
    { question: '¬øCu√°l es el horario?', answer: 'Abrimos de 12:00 a 23:00 todos los d√≠as.' },
  ];

  const [business, setBusiness] = useState<Business>(mockBusinessData);
  const [context, setContext] = useState<string>(mockContext);
  const [faqs, setFaqs] = useState<FAQ[]>(mockFaqs);
  const [message, setMessage] = useState<string>('');

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setBusiness({ ...business, [name]: value });
  };

  const handleTypeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setBusiness({ ...business, type: e.target.value });
  };

  const handleHoursChange = (day: string, field: 'open' | 'close', value: string) => {
    setBusiness({
      ...business,
      hours: {
        ...business.hours,
        [day]: {
          ...business.hours[day],
          [field]: value,
        },
      },
    });
  };

  const handleFaqChange = (index: number, field: 'question' | 'answer', value: string) => {
    const updatedFaqs = [...faqs];
    updatedFaqs[index] = { ...updatedFaqs[index], [field]: value };
    setFaqs(updatedFaqs);
  };

  const addFaq = () => {
    setFaqs([...faqs, { question: '', answer: '' }]);
  };

  const removeFaq = (index: number) => {
    setFaqs(faqs.filter((_, i) => i !== index));
  };

  const handleSave = () => {
    setMessage('Configuraci√≥n guardada con √©xito (simulado)');
    const prompt = `Eres el asistente de ${business.name}, un ${business.type} ubicado en ${business.location}, ${business.address}. Estamos abiertos ${Object.entries(business.hours).map(([day, times]) => `${day} de ${times.open} a ${times.close}`).join(', ')}. Responde amablemente a las consultas de los clientes. Contexto adicional: ${context}. FAQs: ${faqs.map(faq => `Pregunta: ${faq.question}, Respuesta: ${faq.answer}`).join('; ')}`;
    console.log('Simulando env√≠o a n8n:', {
      negocioId: business.id,
      contexto: prompt,
    });
  };

  const days = ['Lunes', 'Martes', 'Mi√©rcoles', 'Jueves', 'Viernes', 'S√°bado', 'Domingo'];

  return (
    <div className="bg-white shadow-md rounded-lg p-6">
      <h2 className="text-2xl font-bold text-black mb-6 font-poppins">
        Configuraci√≥n Principal
      </h2>
      <div className="space-y-4">
        <div className="flex items-center gap-2">
          <span className="text-gray-600">üè™</span>
          <input
            type="text"
            name="name"
            value={business.name}
            onChange={handleInputChange}
            placeholder="La Pizzer√≠a Italiana"
            className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-blue font-poppins"
          />
        </div>
        <select
          value={business.type}
          onChange={handleTypeChange}
          className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-blue font-poppins"
        >
          <option value="" disabled>Seleccionar</option>
          <option value="restaurante">Restaurante</option>
          <option value="veterinaria">Veterinaria</option>
          <option value="consultorio">Consultorio M√©dico</option>
          <option value="personalizado">Personalizado</option>
        </select>
        <div className="flex items-center gap-2">
          <span className="text-gray-600">üìç</span>
          <input
            type="text"
            name="location"
            value={business.location}
            onChange={handleInputChange}
            placeholder="Buenos Aires"
            className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-blue font-poppins"
          />
        </div>
        <div className="flex items-center gap-2">
          <span className="text-gray-600">üìç</span>
          <input
            type="text"
            name="address"
            value={business.address}
            onChange={handleInputChange}
            placeholder="Av. Corrientes 123"
            className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-blue font-poppins"
          />
        </div>
        <div className="border border-gray-300 rounded-lg">
          <div className="flex items-center justify-between p-2 bg-gray-100 rounded-t-lg">
            <div className="flex items-center gap-2">
              <span className="text-gray-600">‚è∞</span>
              <span className="font-poppins">Horarios de Atenci√≥n</span>
            </div>
            <span className="text-gray-600">‚ñº</span>
          </div>
          <div className="p-4">
            {days.map((day) => (
              <div key={day} className="flex items-center gap-4 mb-2">
                <span className="w-24 font-poppins">{day}</span>
                <input
                  type="time"
                  value={business.hours[day].open}
                  onChange={(e) => handleHoursChange(day, 'open', e.target.value)}
                  className="p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-blue"
                />
                <input
                  type="time"
                  value={business.hours[day].close}
                  onChange={(e) => handleHoursChange(day, 'close', e.target.value)}
                  className="p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-blue"
                />
              </div>
            ))}
          </div>
        </div>
        <textarea
          value={context}
          onChange={(e) => setContext(e.target.value)}
          placeholder="Ej: Eres el contestador de la pizzer√≠a Bongusto en Buenos Aires..."
          className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-blue font-poppins"
          rows={4}
        />
        <h3 className="text-xl font-bold text-black mb-4 font-poppins">
          Preguntas Frecuentes (FAQs)
        </h3>
        {faqs.map((faq, index) => (
          <div key={index} className="flex gap-4 mb-4 items-center">
            <input
              type="text"
              value={faq.question}
              onChange={(e) => handleFaqChange(index, 'question', e.target.value)}
              placeholder="Pregunta"
              className="flex-1 p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-blue font-poppins"
            />
            <input
              type="text"
              value={faq.answer}
              onChange={(e) => handleFaqChange(index, 'answer', e.target.value)}
              placeholder="Respuesta"
              className="flex-1 p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-blue font-poppins"
            />
            <button
              onClick={() => removeFaq(index)}
              className="bg-primary-red text-white px-4 py-2 rounded-lg hover:bg-secondary-red font-poppins"
            >
              ELIMINAR
            </button>
          </div>
        ))}
        <button
          onClick={addFaq}
          className="bg-primary-blue text-white px-4 py-2 rounded-lg hover:bg-[#153E6F] font-poppins"
        >
          A√ëADIR FAQ
        </button>
        <button
          onClick={handleSave}
          className="bg-primary-green text-white px-4 py-2 rounded-lg hover:bg-secondary-green mt-4 font-poppins"
        >
          GUARDAR CAMBIOS
        </button>
        {message && (
          <p className={`mt-4 font-poppins ${message.includes('Error') ? 'text-red-500' : 'text-green-500'}`}>
            {message}
          </p>
        )}
      </div>
    </div>
  );
};

export default MainConfig;
