import { useNavigate } from 'react-router-dom';
import {
  Cog6ToothIcon,
  ShoppingCartIcon,
  ChartBarIcon,
} from '@heroicons/react/24/outline';

interface SidebarProps {
  selected: 'config' | 'orders' | 'analytics';
}

const NAV_ITEMS = [
  {
    id: 'config',
    label: 'Configuración',
    icon: Cog6ToothIcon,
    path: '/',
  },
  {
    id: 'orders',
    label: 'Pedidos',
    icon: ShoppingCartIcon,
    path: '/orders',
  },
  {
    id: 'analytics',
    label: 'Analíticas',
    icon: ChartBarIcon,
    path: '/analytics',
  },
];

const Sidebar: React.FC<SidebarProps> = ({ selected }) => {
  const navigate = useNavigate();

  return (
    <aside className="w-52 bg-[#f2f5fa] min-h-screen p-4">
      <nav className="flex flex-col gap-4">
        {NAV_ITEMS.map(({ id, label, icon: Icon, path }) => {
          const isActive = selected === id;
          return (
            <div key={id} className="relative overflow-hidden">
              <button
                onClick={() => navigate(path)}
                className={`relative flex items-center gap-3 px-4 py-3 font-poppins transition-colors group
                  w-[calc(100%+16px)] -mr-4 z-10
                  ${
                    isActive
                      ? 'bg-white text-blue-800 font-semibold rounded-l-xl rounded-r-none shadow-[-2px_2px_8px_0px_rgba(0,0,0,0.90)]'
                      : 'bg-[#273168] hover:bg-[#3b4484] text-blue-100 rounded-l-xl rounded-r-none shadow-[inset_-11px_0_16px_-6px_rgba(10,10,16,0.75),-2px_2px_8px_0_rgba(0,0,0,0.30)]'
                  }
                `}
              >
                <Icon
                  className={`h-6 w-6 transition-colors ${
                    isActive ? 'text-blue-700' : 'text-blue-200'
                  }`}
                />
                <span
                  className={`text-lg ${
                    isActive ? 'text-blue-800' : 'text-blue-100'
                  }`}
                >
                  {label}
                </span>
              </button>
              {/* Capa para "cortar" la sombra en el lado derecho cuando está activo */}
              {isActive && (
                <div className="absolute inset-0 z-0 pointer-events-none">
                  <div className="absolute top-0 bottom-0 right-0 w-3 bg-[#f2f5fa]" />
                </div>
              )}
            </div>
          );
        })}
      </nav>
    </aside>
  );
};

export default Sidebar;
