import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, IconButton, Menu, MenuItem } from '@mui/material';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';
import SettingsIcon from '@mui/icons-material/Settings';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';
import logo from '../assets/LogoAutoWhappBlanco.png';
import axios from 'axios';

interface Negocio {
  id: number;
  nombre: string;
}

const Header: React.FC<{ onNegocioChange: (negocioId: number) => void }> = ({ onNegocioChange }) => {
  const [negocios, setNegocios] = useState<Negocio[]>([]);
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const [selectedNegocioId, setSelectedNegocioId] = useState<number | null>(null);

  // Obtener la lista de negocios al cargar el componente
  useEffect(() => {
    axios.get<Negocio[]>('http://localhost:3000/negocios')
      .then(response => {
        setNegocios(response.data);
        if (response.data.length > 0) {
          const firstNegocioId = response.data[0].id;
          setSelectedNegocioId(firstNegocioId);
          onNegocioChange(firstNegocioId); // Selecciona el primer negocio por defecto
        }
      })
      .catch(error => {
        console.error('Error al obtener los negocios:', error);
      });
  }, [onNegocioChange]);

  // Abrir el menú al hacer clic en el botón de perfil
  const handleMenuOpen = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  // Cerrar el menú
  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  // Manejar la selección de un negocio
  const handleNegocioSelect = (negocioId: number) => {
    setSelectedNegocioId(negocioId);
    onNegocioChange(negocioId); // Pasar el negocioId al componente padre
    handleMenuClose();
  };

  return (
    <AppBar
      position="sticky"
      sx={{
        background: 'linear-gradient(90deg, rgb(69, 79, 255) 17%, rgb(255, 255, 255) 28%)',
        boxShadow:
          '0 6px 24px -8px rgba(50,60,130,0.5), 0 5px 3px 0 rgba(30,40,90,0.5)',
        zIndex: 1300,
      }}
    >
      <Toolbar>
        <img
          src={logo}
          alt="Logo AutoWhapp"
          style={{
            height: 40,
            width: 40,
            marginRight: 16,
            objectFit: 'contain',
          }}
        />
        <Typography
          variant="h6"
          sx={{
            flexGrow: 1,
            fontFamily: 'Poppins',
            fontWeight: 'bold',
          }}
        >
          AutoWhapp
        </Typography>
        <IconButton
          sx={{
            backgroundColor: 'rgb(69, 79, 225)',
            marginRight: 1,
            '&:hover': { backgroundColor: '#93C5FD' },
          }}
        >
          <HelpOutlineIcon sx={{ color: '#FFFFFF' }} />
        </IconButton>
        <IconButton
          sx={{
            backgroundColor: 'rgb(69, 79, 225)',
            marginRight: 1,
            '&:hover': { backgroundColor: '#93C5FD' },
          }}
        >
          <SettingsIcon sx={{ color: '#FFFFFF' }} />
        </IconButton>
        {/* Botón de perfil con menú desplegable */}
        <IconButton
          sx={{
            backgroundColor: 'rgb(69, 79, 225)',
            '&:hover': { backgroundColor: '#93C5FD' },
          }}
          onClick={handleMenuOpen}
        >
          <PersonOutlineIcon sx={{ color: '#FFFFFF' }} />
        </IconButton>
        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleMenuClose}
          PaperProps={{
            sx: {
              backgroundColor: 'rgb(69, 79, 225)',
              color: '#FFFFFF',
            },
          }}
        >
          {negocios.length > 0 ? (
            negocios.map(negocio => (
              <MenuItem
                key={negocio.id}
                onClick={() => handleNegocioSelect(negocio.id)}
                sx={{
                  fontFamily: 'Poppins',
                  '&:hover': { backgroundColor: '#93C5FD' },
                }}
              >
                {negocio.nombre}
              </MenuItem>
            ))
          ) : (
            <MenuItem disabled>Sin negocios disponibles</MenuItem>
          )}
        </Menu>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
