import React, { useState } from 'react';
import { Card, CardContent, Typography, Switch } from '@mui/material';

const BotStatus: React.FC = () => {
  const [isActive, setIsActive] = useState<boolean>(true);
  const [message, setMessage] = useState<string>('');

  const handleToggle = () => {
    const newStatus = !isActive;
    setIsActive(newStatus);
    // Simular el guardado y la notificación a n8n
    setMessage(`Bot ${newStatus ? 'encendido' : 'apagado'} con éxito (simulado)`);
    console.log('Simulando envío a n8n:', { isActive: newStatus });
  };

  return (
    <Card 
    sx={{ 
      boxShadow: '0 4px 12px rgba(0,0,0,0.10)', 
      borderRadius: '16px', 
      px: 3, py: 1,
      minWidth: 340,
      width: "100%",
      maxWidth: "480px"
    }}
  >
    <CardContent sx={{ 
      p: 1, 
    }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: 18, flexWrap: 'wrap' }}>
        <Typography
          variant="h6"
          sx={{ fontFamily: 'Poppins', fontWeight: 'bold', minWidth: 130, pr: 3 }}
        >
          Estado del Bot
        </Typography>
        <Switch checked={isActive} onChange={handleToggle} />
        <Typography sx={{ fontFamily: 'Poppins', ml: 1 }}>
          {isActive ? 'Encendido' : 'Apagado'}
        </Typography>
      </div>
      {message && (
        <Typography
          sx={{
            color: message.includes('Error') ? 'red' : 'green',
            mt: 1, ml: 0.5, fontSize: 15,
          }}
        >
          {message}
        </Typography>
      )}
    </CardContent>
  </Card>
  );
};

export default BotStatus;
