import React, { useState, FormEvent } from 'react';
import axios from 'axios';
import './App.css';
import { FAQ } from './types';

const App: React.FC = () => {
  const [question, setQuestion] = useState<string>('');
  const [answer, setAnswer] = useState<string>('');
  const [message, setMessage] = useState<string>('');

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    if (!question.trim() || !answer.trim()) {
      setMessage('Por favor, completa ambos campos');
      return;
    }
    const faq: FAQ = { question, answer };
    try {
      const webhookUrl: string = 'http://localhost:5678/webhook/faq';
      const response = await axios.post(webhookUrl, faq);
      setMessage('FAQ guardada con éxito');
      setQuestion('');
      setAnswer('');
    } catch (error) {
      setMessage('Error al guardar la FAQ');
      console.error(error);
    }
  };

  return (
    <div className="App">
      <h1 className="title">AutoWhapp - Configuración</h1>
      <form className="faq-form" onSubmit={handleSubmit}>
        <div className="input-group">
          <label className="label">Pregunta:</label>
          <input
            type="text"
            className="input"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="Escribe la pregunta..."
          />
        </div>
        <div className="input-group">
          <label className="label">Respuesta:</label>
          <input
            type="text"
            className="input"
            value={answer}
            onChange={(e) => setAnswer(e.target.value)}
            placeholder="Escribe la respuesta..."
          />
        </div>
        <button type="submit" className="submit-btn">Guardar FAQ</button>
      </form>
      {message && (
        <p className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </p>
      )}
    </div>
  );
};

export default App;
