import React, { useState } from 'react';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import BotStatus from '../components/BotStatus';
import MainConfig from '../components/MainConfig';
import Products from '../components/Products';

const ConfigPage: React.FC = () => {
  const [negocioId, setNegocioId] = useState<number | null>(null);

  const handleNegocioChange = (negocioId: number) => {
    setNegocioId(negocioId);
  };

  return (
    <div>
      {/* Header con selector de negocio */}
      <Header onNegocioChange={handleNegocioChange} />
      <div className="flex">
        <Sidebar selected="config" />
        <div className="flex-grow bg-blue-600 p-6 min-h-screen">
          {negocioId ? (
            <>
              {/* Título y BotStatus */}
              <div className="flex justify-between items-start mb-6">
                <h2 className="text-2xl font-poppins font-bold text-white mt-3">
                  Configuración de Chatbot
                </h2>
                <div className="flex-1 flex justify-end max-w-xl">
                  {/* Aquí deberías pasar el valor real de active, lo explico abajo */}
                  <BotStatus
                    negocioId={negocioId}
                    active={true}
                    onToggle={() => {}} 
                  />
                </div>
              </div>
              {/* MainConfig y Products */}
              <div className="flex gap-3 flex-wrap">
                <div className="flex-1 md:flex-[0_0_100%]">
                  <MainConfig negocioId={negocioId} />
                </div>
                <div className="flex-1 md:flex-[0_0_100%]">
                  <Products />
                </div>
              </div>
            </>
          ) : (
            <div className="text-white font-poppins text-lg">
              Seleccione un negocio para comenzar
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ConfigPage;
