import React, { useState } from 'react';
import { Order } from '../types';

const Orders: React.FC = () => {
  const mockOrders: Order[] = [
    { id: 1, time: '14:30', status: 'Recibido', client: 'Juan Pérez', phone: '+541123456789', items: 'Pizza Margherita', total: '$1500' },
    { id: 2, time: '15:00', status: 'Preparando', client: 'Ana Gómez', phone: '+541198765432', items: 'Coca-Cola', total: '$500' },
  ];

  const [orders, setOrders] = useState<Order[]>(mockOrders);
  const [openModal, setOpenModal] = useState<boolean>(false);
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [orderMessage, setOrderMessage] = useState<string>('Tu pedido ha sido actualizado.');
  const [message, setMessage] = useState<string>('');
  const [filter, setFilter] = useState<'Todos' | 'Recibido' | 'Preparando' | 'Enviado'>('Todos');

  // Depuración: Verificar el estado inicial de orders
  console.log('Orders iniciales:', orders);
  console.log('Filtro actual:', filter);
  console.log('Pedidos filtrados:', orders.filter(order => filter === 'Todos' || order.status === filter));

  const handleOpenModal = (order: Order) => {
    setSelectedOrder(order);
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setSelectedOrder(null);
  };

  const updateStatus = (orderId: number, newStatus: 'Recibido' | 'Preparando' | 'Enviado') => {
    setOrders(orders.map(order => order.id === orderId ? { ...order, status: newStatus } : order));
    setMessage(`Estado del pedido ${orderId} actualizado a ${newStatus} (simulado)`);
    console.log('Simulando envío a n8n:', {
      pedidoId: orderId,
      nuevoEstado: newStatus,
      mensaje: orderMessage || 'Tu pedido ha sido actualizado.',
    });
  };

  const saveOrderMessage = () => {
    setMessage('Mensaje de pedido guardado con éxito (simulado)');
    console.log('Simulando envío a n8n:', { mensaje: orderMessage });
  };

  const addNewOrder = () => {
    const newOrder: Order = {
      id: orders.length + 1,
      time: new Date().toLocaleTimeString(),
      status: 'Recibido',
      client: 'Cliente Nuevo',
      phone: '+541123456789',
      items: 'Producto de prueba',
      total: '$1000',
    };
    setOrders([...orders, newOrder]);
    setMessage('Pedido añadido con éxito (simulado)');
  };

  const refreshOrders = () => {
    setOrders(mockOrders);
    setMessage('Pedidos actualizados (simulado)');
  };

  const filteredOrders = filter === 'Todos' ? orders : orders.filter(order => order.status === filter);

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-800 mb-6 font-poppins">
        Módulo de Pedidos
      </h2>
      {orders.length === 0 ? (
        <p className="text-gray-600 font-poppins">No hay pedidos disponibles.</p>
      ) : (
        <div className="space-y-4">
          <input
            type="text"
            value={orderMessage}
            onChange={(e) => setOrderMessage(e.target.value)}
            placeholder="Ej: Tu pedido ha sido recibido, te avisaremos pronto..."
            className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 font-poppins"
          />
          <button
            onClick={saveOrderMessage}
            className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 font-poppins"
          >
            Guardar Mensaje
          </button>
          <div className="flex justify-between items-center mb-4">
            <input
              type="text"
              placeholder="Buscar pedidos..."
              className="w-72 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 font-poppins"
            />
            <div className="flex gap-2">
              <button
                onClick={() => setFilter('Todos')}
                className={`px-4 py-2 rounded-lg font-poppins ${
                  filter === 'Todos' ? 'bg-blue-900 text-white' : 'bg-gray-200 text-gray-800 hover:bg-blue-200'
                }`}
              >
                Todos
              </button>
              <button
                onClick={() => setFilter('Recibido')}
                className={`px-4 py-2 rounded-lg font-poppins ${
                  filter === 'Recibido' ? 'bg-blue-900 text-white' : 'bg-gray-200 text-gray-800 hover:bg-blue-200'
                }`}
              >
                Recibidos
              </button>
              <button
                onClick={() => setFilter('Enviado')}
                className={`px-4 py-2 rounded-lg font-poppins ${
                  filter === 'Enviado' ? 'bg-blue-900 text-white' : 'bg-gray-200 text-gray-800 hover:bg-blue-200'
                }`}
              >
                Enviados
              </button>
              <button
                onClick={refreshOrders}
                className="bg-blue-900 text-white px-4 py-2 rounded-lg hover:bg-blue-800 flex items-center gap-2 font-poppins"
              >
                Actualizar
              </button>
              <button
                onClick={addNewOrder}
                className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 flex items-center gap-2 font-poppins"
              >
                Nuevo Pedido
              </button>
            </div>
          </div>
          {filteredOrders.length === 0 ? (
            <p className="text-gray-600 font-poppins">No hay pedidos que coincidan con el filtro.</p>
          ) : (
            filteredOrders.map(order => (
              <div key={order.id} className="bg-white shadow-md rounded-lg p-4 mb-4 flex justify-between items-center">
                <div>
                  <h3 className="text-lg font-bold text-gray-800 font-poppins">Pedido {order.id}</h3>
                  <p className="text-gray-600 font-poppins">{order.time}</p>
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() => updateStatus(order.id, 'Recibido')}
                    className={`px-4 py-2 rounded-lg text-white font-poppins ${
                      order.status === 'Recibido' ? 'bg-gray-400' : 'bg-gray-200 text-gray-800'
                    }`}
                  >
                    Recibido
                  </button>
                  <button
                    onClick={() => updateStatus(order.id, 'Preparando')}
                    className={`px-4 py-2 rounded-lg text-white font-poppins ${
                      order.status === 'Preparando' ? 'bg-yellow-500' : 'bg-gray-200 text-gray-800'
                    }`}
                  >
                    Preparando
                  </button>
                  <button
                    onClick={() => updateStatus(order.id, 'Enviado')}
                    className={`px-4 py-2 rounded-lg text-white font-poppins ${
                      order.status === 'Enviado' ? 'bg-green-500' : 'bg-gray-200 text-gray-800'
                    }`}
                  >
                    Enviado
                  </button>
                  <button
                    onClick={() => handleOpenModal(order)}
                    className="bg-blue-900 text-white px-4 py-2 rounded-lg hover:bg-blue-800 font-poppins"
                  >
                    Ver Detalles
                  </button>
                </div>
              </div>
            ))
          )}
          {openModal && selectedOrder && (
            <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
              <div className="bg-white rounded-lg p-6 w-96">
                <h3 className="text-lg font-bold text-gray-800 mb-4 font-poppins">Detalles del Pedido</h3>
                <p className="font-poppins">Cliente: {selectedOrder.client}</p>
                <p className="font-poppins">Teléfono: {selectedOrder.phone}</p>
                <p className="font-poppins">Productos: {selectedOrder.items}</p>
                <p className="font-poppins">Total: {selectedOrder.total}</p>
                <button
                  onClick={handleCloseModal}
                  className="mt-4 bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 font-poppins"
                >
                  Cerrar
                </button>
              </div>
            </div>
          )}
          {message && (
            <p className={`mt-4 font-poppins ${message.includes('Error') ? 'text-red-500' : 'text-green-500'}`}>
              {message}
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default Orders;
